function decorate(block) {
    // Deconstruct the first two divs inside the generated by AEM block
    const [div1, div2] = block.children;
    div1.className = 'job-seeker-intro__picture';
    const child = div1.querySelector('div');
    if (child)
        child.className = 'job-seeker-intro__inner';
}
export default decorate;
